from dataclasses import dataclass

from diamond_miner.defaults import UNIVERSE_SUBSET
from diamond_miner.queries.fragments import date_time
from diamond_miner.queries.query import Query, StoragePolicy, weight_table
from diamond_miner.typing import IPNetwork


@dataclass(frozen=True)
class CreateWeightTable(Query):

    SORTING_KEY = "round, cluster_id, probe_ttl"

    storage_policy: StoragePolicy = StoragePolicy()

    def statement(
        self, measurement_id: str, subset: IPNetwork = UNIVERSE_SUBSET
    ) -> str:
        return f"""
        CREATE TABLE IF NOT EXISTS {weight_table(measurement_id)}
        (
            cluster_id             String, 
            probe_ttl              UInt8, 
            probe_dst_prefix       IPv6, 
            proportion             Float32,
            round                  UInt8
        )
        ENGINE MergeTree
        ORDER BY ({self.SORTING_KEY})
        TTL {date_time(self.storage_policy.archive_on)} TO VOLUME '{self.storage_policy.archive_to}'
        SETTINGS storage_policy = '{self.storage_policy.name}'
        """
